, AND o.location LIKE concat('%', #{keyWord}, '%') OR o.description LIKE concat('%', #{keyWord}, '%') OR o.user_description LIKE concat('%', #{keyWord}, '%') OR u.name LIKE concat('%', #{keyWord}, '%') OR op.name LIKE concat('%', #{keyWord}, '%')
ORDER BY o.status ASC LIMIT #{start}, #{pageSize}

WHERE op.id = #{id}
, AND o.location LIKE concat('%', #{keyWord}, '%') OR o.description LIKE concat('%', #{keyWord}, '%') OR o.user_description LIKE concat('%', #{keyWord}, '%') OR u.name LIKE concat('%', #{keyWord}, '%') OR op.name LIKE concat('%', #{keyWord}, '%')
ORDER BY o.status ASC LIMIT #{start}, #{pageSize}
